import React, { Component } from 'react';
import axios from 'axios';

export class PayrollEmployeeList extends Component {
  static displayName = PayrollEmployeeList.name;
  
  // eslint-disable-next-line no-useless-constructor
  constructor (props) {
    super(props);
  }

  UNSAFE_componentWillMount() {
    this.LoadData();
  }
  
  LoadData () {
    let config = {
      headers: {
        'Access-Control-Allow-Origin' : '*',
        'contentType': 'application/json',
      }
    }

    axios
    .get("http://localhost:52688/Payroll/Employee/Get", config)
    .then(data => 
      { 
        this.employeeList = data.data.map((employee) =>
          <li key={employee}>{employee.Lastname}</li>
        );
        console.log(this.employeeList);
        this.render(this.employeeList);
      })
    .catch(error => console.log(error));
  }

  New () {
    let config = {
      headers: {
        'Access-Control-Allow-Origin' : '*',
        'contentType': 'application/json',
      }
    }

    axios
    .get("http://localhost:52688/Payroll/Employee/Add", config)
    .then(data => 
      { 
        console.log(data);
      })
    .catch(error => console.log(error));
  }
  
  render (employeeList) {
    return (
      <div id="test">
          <h2>Employee List</h2>
          <ul>
          {employeeList}
          </ul>
          <br></br><br></br>

          <input type="button" value="New" title="Add New Employee" className="btn-primary" onClick={this.New}></input>
          <br></br>
      </div>
    );
  }
}
